import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import { Calculator } from './pages/Calculator';
import { ModernCalculator } from './pages/ModernCalculator';
import { AppLayout } from './components/Layout/AppLayout';
import { ThemeToggle } from './components/ThemeToggle/ThemeToggle';

function App() {
  const [showThemeToggle, setShowThemeToggle] = React.useState(true);

  return (
    <BrowserRouter>
      <AppLayout>
        <Routes>
          <Route path="/" element={
            <>
              {React.useEffect(() => setShowThemeToggle(false), [])}
              <ModernCalculator />
            </>
          } />
          <Route path="/old" element={
            <>
              {React.useEffect(() => setShowThemeToggle(true), [])}
              <Calculator />
            </>
          } />
          <Route path="/monsterluk/plexi_appk" element={<Calculator />} />
        </Routes>
        {showThemeToggle && <ThemeToggle />}
      </AppLayout>
    </BrowserRouter>
  );
}

export default App;
