const calculateProduct = (dimensions, options, material, thickness, quantity, productType) => {
  const {
    width,
    height,
    depth = 0
  } = dimensions;
  let surface = 0;
  let components = [];

  // Obliczanie powierzchni według typu produktu
  switch (productType) {
    case 'impuls-kasowy':
