      surface += width * height / 1e6;
      components.push({
        name: 'Plecy',
        surface: width * height / 1e6
      });
      // Boki
      surface += 2 * (depth * height) / 1e6;
      components.push({
        name: 'Boki (2x)',
        surface: 2 * (depth * height) / 1e6
      });
      // Półki
      surface += shelvesCount * (width * depth) / 1e6;
      components.push({
        name: `Półki (${shelvesCount}x)`,
        surface: shelvesCount * (width * depth) / 1e6
      });
      break;
    case 'pojemnik':
      // Dno
      surface += width * depth / 1e6;
      components.push({
        name: 'Dno',
        surface: width * depth / 1e6
      });
      // Ściany
      surface += 2 * (width * height) / 1e6;
      surface += 2 * (depth * height) / 1e6;
      components.push({
        name: 'Ściany',
        surface: (2 * (width * height) + 2 * (depth * height)) / 1e6
      });
      // Wieko opcjonalne
      if (options.hasLid) {
        surface += width * depth / 1e6;
        components.push({
          name: 'Wieko',
          surface: width * depth / 1e6
        });
      }
      break;
    case 'formatka':
      surface = width * height / 1e6;
      components.push({
        name: 'Formatka',
        surface: width * height / 1e6
      });
      break;
    default:
      // Domyślnie prostokąt
      surface = width * height / 1e6;
      components.push({
        name: 'Element',
        surface: width * height / 1e6
      });
  }

  // Obliczenia
  const totalSurface = surface * quantity;
  const surfaceWithWaste = totalSurface * 1.1; // 10% odpadu
  const weight = surface * (thickness / 1000) * material.density; // kg na sztukę
